def smallest_divisor(arr):
    arr.sort()
    min_diff = float('inf')
    for i in range(1, len(arr)):
        if arr[i] % arr[i - 1] == 0 and arr[i] - arr[i - 1] < min_diff:
            min_diff = arr[i] - arr[i - 1]
    if min_diff == float('inf'):
        return 0
    for d in range(1, min_diff + 1):
        beautiful = True
        for i in range(1, len(arr)):
            if (arr[i] % d == 0 and arr[i - 1] % d == 0) or (arr[i] % d != 0 and arr[i - 1] % d != 0):
                beautiful = False
                break
        if beautiful:
            return d
    return 0
