def beautiful_coloring(arr):
    n = len(arr)

    for d in range(1, max(arr)+1):
        blue = False
        flag = True
        for i in range(n):
            if arr[i] % d == 0:
                if blue:
                    flag = False
                    break
                blue = True
            else:
                if not blue:
                    flag = False
                    break
                blue = False 

        if flag:
            return d

    return 0
