def ba_string(k, x, s):
    k, x = int(k), int(x)
    count_stars = s.count('*')
    for _ in range(count_stars):
        for i in range(k + 1):
            if x - pow(k + 1, count_stars - 1) <= 0:
                s = s.replace('*', 'b' * i, 1)
                break
            else:
                x -= pow(k + 1, count_stars - 1)
        count_stars -= 1
    return s.replace('*', '')
