def BA_string(k, x, s):
    b = "b" * (k + 1)
    s = list(s)
    asterisks = s.count('*')
    k += 1
    while asterisks > 0:
        for i in range(k - 1, -1, -1):
            if x <= (k ** (asterisks - 1)) * i:
                s[s.index('*')] = b[:i]
                break
            else:
                x -= (k ** (asterisks - 1)) * i
        asterisks -= 1
    return ''.join(s)
