def binary_xor(str1, str2):
    # Equalize lengths by prefixing the shorter string with zeros
    if len(str1) < len(str2):
        str1 = '0' * (len(str2) - len(str1)) + str1
    elif len(str1) > len(str2):
        str2 = '0' * (len(str1) - len(str2)) + str2

    # Perform binary XOR operation
    return ''.join(['0' if str1[i] == str2[i] else '1' for i in range(len(str1))])
