def interval_intersection(int1, int2):
    int1 = list(map(int, int1.replace('(', '').replace(')', '').split(',')))
    int2 = list(map(int, int2.replace('(', '').replace(')', '').split(',')))

    lower_boundary = max(int1[0], int2[0])
    upper_boundary = min(int1[1], int2[1])

    if upper_boundary - lower_boundary < 1:
        return lower_boundary
    else:
        return (lower_boundary, upper_boundary)
