def calculate_expression(values, operations):
    result = values[0]
    for value, operation in zip(values[1:], operations):
        if operation == '+':
            result += value
        elif operation == '*':
            result *= value
        elif operation == '-':
            result -= value
        elif operation == '**':
            result **= value
    return result

# test the function
print(calculate_expression([2, 3, 4, 5], ['+', '*', '-']))  # 9
print(calculate_expression([2, 3, 4, 5], ['**', '*', '+']))  # 37
