def calculate_expression(numbers, operations):
    numbers = eval(numbers[0])
    operations = eval(operations[0])
    result = numbers[0]
    for i in range(1, len(numbers)):
        if operations[i-1] == '+':
            result += numbers[i]
        elif operations[i-1] == '-':
            result -=  numbers[i]
        elif operations[i-1] == '*':
            result *=  numbers[i]
        elif operations[i-1] == '/':
            result /=  numbers[i]
        elif operations[i-1] == '**':
            result **= numbers[i]
    return result
