def convert_to_base(num, base):
    if num == 0:
        return '0'
    digits = []
    while num:
        digits.append(int(num % base))
        num //= base
    return ''.join(str(x) for x in digits[::-1])

print(convert_to_base(8, 3)) # Expected output '22'
print(convert_to_base(8, 2)) # Expected output '100'
