def caesar_cipher(text, shift):
    result = []

    for char in text:
        if char.isalpha():
            stay_in_alphabet = ord(char) + shift
            if char.isupper():
                new_char = chr(stay_in_alphabet if stay_in_alphabet <= ord('Z') else stay_in_alphabet - 26)
            else:
                new_char = chr(stay_in_alphabet if stay_in_alphabet <= ord('z') else stay_in_alphabet - 26)
            result.append(new_char)
        else:
            result.append(char)

    return "".join(result)
