def caesar_cipher(input_string, shift):
    cipher_text = ''
    for char in input_string:
        if char.isalpha():
            ascii_shift = 65 if char.isupper() else 97
            cipher_char = chr((ord(char) - ascii_shift + shift) % 26 + ascii_shift)
            cipher_text += cipher_char
        else:
            cipher_text += char
    return cipher_text
