import re

def base_url(url):
    """
    Returns the base url given a full url.
    Returns the url up to the last slash that appears after the initial http:// or https://.
    """
    match = re.search(r'(http[s]?://[^/]*[^/]*/)', url)
    if match is None:
        raise ValueError("Invalid URL")
    else:
        return match.group()
