import re

def base_url(url):
    """ 
    Returns the base url given a full url
    """
    base_url = re.findall('http[s]?://(?:[a-zA-Z]|[0-9]|[$-_@.&+]|[!*\\(\\),]|(?:%[0-9a-fA-F][0-9a-fA-F]))+/', url)
    if base_url:
        return base_url[0]
    else:
        base_url = re.findall('http[s]?://(?:[a-zA-Z]|[0-9]|[$-_@.&+]|[!*\\(\\),]|(?:%[0-9a-fA-F][0-9a-fA-F]))+', url)
        if base_url:
            return base_url[0] + '/'
        else:
            return None
