from datetime import datetime
from dateutil import tz

def parse_iso8601(date_str, delimiter='T', timezone=None): 
    """ Return a UNIX timestamp from the given date.
        Delimiter corresponds to the time delimiter (e.g. 2023-10-19T00:00:00+0000)
    """
    try:
        if date_str.endswith('Z'):
            date_str = date_str[:-1] + '+0000'

        date_time_obj = datetime.strptime(date_str, '%Y-%m-%d{}%H:%M:%S%z'.format(delimiter))
        timestamp_str = date_time_obj.timestamp()
        return str(int(timestamp_str))
    except:
        return None
