def asc_desc_ending_sorting(n, m, arr):
    def compare(a, b):
        for i in range(m):
            if i % 2 == 0:
                if a[i] == b[i]:
                    continue
                elif a[i] < b[i]:
                    return -1
                else:
                    return 1
            else:
                if a[i] == b[i]:
                    continue
                elif a[i] > b[i]:
                    return -1
                else:
                    return 1
        return 0

    arr = list(enumerate(arr, start=1)) 
    arr.sort(key=lambda x: x[1], cmp=compare) 
    return [x[0] for x in arr]
