def asc_desc_sort(n, m, book_list):

    # Define custom sorting function
    def compare(a, b):
        for i in range(m):
            if a[1][i] != b[1][i]:
                if i%2 == 0:
                    return ord(a[1][i]) - ord(b[1][i])
                else:
                    return ord(b[1][i]) - ord(a[1][i])
        return 0

    # Enumerate list for index keeping, sort using the custom function and extract indices, add 1 because lists in Python start at 0
    return [i[0] + 1 for i in sorted(enumerate(book_list), key=functools.cmp_to_key(compare))]
