def minimal_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    max_duration_in_a_concert = total_duration // 2 
    dp = [0] * (max_duration_in_a_concert + 1)
    for i in range(c):
        for j in range(max_duration_in_a_concert, 2, -1):
            dp[j] = max(dp[j], dp[j - 3] + 3 if j >= 3 else 0)
    for i in range(b):
        for j in range(max_duration_in_a_concert, 1, -1):
            dp[j] = max(dp[j], dp[j - 2] + 2 if j >= 2 else 0)
    for i in range(a):
        for j in range(max_duration_in_a_concert, 0, -1):
            dp[j] = max(dp[j], dp[j - 1] + 1 if j >= 1 else 0)
    return total_duration - 2 * dp[max_duration_in_a_concert]
