def minimal_difference(a, b, c):
    concert_songs_duration = [a + b * 2 + c * 3]
    total_songs_duration = sum(concert_songs_duration)
    half_songs_duration = total_songs_duration // 2
    for i in range(c + 1):
        for j in range(min(i + b, half_songs_duration // 2) + 1):
            temp = i * 3 + j * 2
            concert_songs_duration.append(temp)
            concert_songs_duration.append(total_songs_duration - temp)
    concert_songs_duration.sort()
    return min(b - a for a, b in zip(concert_songs_duration, concert_songs_duration[1:]))
