def minimal_difference(a, b, c):
    # List of all songs
    song_lengths = [1] * a + [2] * b + [3] * c
    n = len(song_lengths)
    # Calculate the total duration of all songs
    total_duration = sum(song_lengths)
    # Initialize dp_table with False
    dp_table = [[0 for _ in range((total_duration // 2) + 1)] for _ in range(n + 1)]
    for i in range(n + 1):
        dp_table[i][0] = True
    # Populate dp_table
    for i in range(1, n + 1):
        for j in range(1, (total_duration // 2) + 1):
            if j < song_lengths[i - 1]:
                dp_table[i][j] = dp_table[i - 1][j]
            else:
                dp_table[i][j] = dp_table[i - 1][j] or \
                    dp_table[i - 1][j - song_lengths[i - 1]]
    for i in range((total_duration // 2), -1, -1):
        if dp_table[n][i]:
            return total_duration - 2 * i
