def min_diff(a,b,c):
    total_time = a + 2*b + 3*c
    half_time = total_time//2
    dp = [0]*(half_time+1)     
    for i in range(a):
        for t in range(half_time, 0, -1):
            if t >= 1: dp[t] = max(dp[t], dp[t-1] + 1)
    for i in range(b):
        for t in range(half_time, 0, -1):
            if t >= 2: dp[t] = max(dp[t], dp[t-2] + 2)
    for i in range(c):
        for t in range(half_time, 0, -1):
            if t >=3: dp[t] = max(dp[t], dp[t-3] + 3)
    return abs(total_time - 2*dp[half_time])
