def min_concert_diff(a, b, c):
    total_duration = a + 2*b + 3*c

    # initialize dp list
    dp = [0] * (total_duration//2 + 1)

    # consider the duration of one-minute song
    for i in range(a, -1, -1):
        for j in range(i):
            dp[j] = max(dp[j], dp[j-i] + i)

    # consider the duration of two-minute song
    for i in range(b, -1, -1):
        for j in range(2*i, -1, -2):
            dp[j] = max(dp[j], dp[j-2*i] + 2*i)

    # consider the duration of three-minute song
    for i in range(c, -1, -1):
        for j in range(3*i, -1, -3):
            dp[j] = max(dp[j], dp[j-3*i] + 3*i)

    # return the minimum absolute difference
    return abs(total_duration - 2*dp[total_duration//2])
