def minimal_duration_diff(a, b, c):
    total_minutes = a + 2*b + 3*c
    dp = [0]*(total_minutes//2 + 1)
    for i in range(a):
        for j in range(total_minutes//2, 0, -1):
            if j >= 1:
                dp[j] = max(dp[j], dp[j - 1] + 1)
    for i in range(b):
        for j in range(total_minutes//2, 1, -1):
            if j >= 2:
                dp[j] = max(dp[j], dp[j - 2] + 2)
    for i in range(c):
        for j in range(total_minutes//2, 2, -1):
            if j >= 3:
                dp[j] = max(dp[j], dp[j - 3] + 3)
    return total_minutes - 2*dp[total_minutes//2]
