def min_difference(a, b, c):
    total_time = a + 2*b + 3*c
    half_time = total_time//2
    dp = [0]*(half_time+1)
    for i in range(a, half_time+1):
        dp[i] = max(dp[i], dp[i-a] + a)
    for i in range(2*b, half_time+1):
        dp[i] = max(dp[i], dp[i-2*b] + 2*b)
    for i in range(3*c, half_time+1):
        dp[i] = max(dp[i], dp[i-3*c] + 3*c)
    return total_time - 2*dp[half_time]
