def find_winner(A):
    alice_turn = True
    last_number = -1
    while len(A) > 0:
        if A[0] > A[-1] and A[0] > last_number:
            last_number = A.pop(0)
        elif A[-1] > last_number:
            last_number = A.pop(-1)
        elif A[0] > last_number:
            last_number = A.pop(0)
        else:
            break

        alice_turn = not alice_turn

    return 'Alice' if alice_turn else 'Bob'
