def game_winner(A):
    i, j, last, alice_turn = 0, len(A) - 1, -1, True
    while i <= j:
        if min(A[i], A[j]) > last:
            last = min(A[i], A[j])
            if A[i] < A[j]:
                i += 1
            else:
                j -= 1
        elif max(A[i], A[j]) > last:
            last = max(A[i], A[j])
            if A[i] > A[j]:
                i += 1
            else:
                j -= 1
        else:
            break
        alice_turn = not alice_turn
    return 'Alice' if alice_turn else 'Bob'
