def determine_winner(array):
    n = len(array)
    dp = [[0]*n for _ in range(n)]
    for width in range(n):
        for left in range(n-width):
            right = left+width
            parity = (right-left+n) % 2
            if parity == 0:
                dp[left][right] = max(
                    dp[left+1][right]+array[left],
                    dp[left][right-1]+array[right]
                )
            else:
                dp[left][right] = min(
                    dp[left+1][right],
                    dp[left][right-1]
                )
    alice_score = dp[0][n-1]
    total_sum = sum(array)
    bob_score = total_sum - alice_score
    return 'Alice' if alice_score > bob_score else 'Bob'
