def determine_winner(A):
    Alice_turn = True
    last_pick = -1

    while len(A) > 0:
        if A[0] > last_pick and (A[-1] <= last_pick or A[0] <= A[-1]):
            last_pick = A.pop(0)
            Alice_turn = not Alice_turn
        elif A[-1] > last_pick and (A[0] <= last_pick or A[-1] > A[0]):
            last_pick = A.pop()
            Alice_turn = not Alice_turn
        else:
            break

    if Alice_turn:
        return 'Bob'
    else:
        return 'Alice'
