def determine_winner(A):
    def can_take(A, last, left, right):
        return (left <= right) and (last < A[left] or last < A[right])

    n = len(A)
    dp = [[0 for _ in range(n + 1)] for _ in range(n + 1)]
    nxt = [[0 for _ in range(n + 1)] for _ in range(n + 1)]
    for length in range(1, n + 1):
        for left in range(n - length + 1):
            right = left + length - 1
            if not can_take(A, A[left - 1], left, right):
                dp[left][right] = dp[left - 1][right] + 1
                nxt[left][right] = left - 1
            elif not can_take(A, A[right + 1], left, right):
                dp[left][right] = dp[left][right + 1] + 1
                nxt[left][right] = right + 1
            else:
                if dp[left - 1][right] < dp[left][right + 1]:
                    dp[left][right] = dp[left - 1][right] + 1
                    nxt[left][right] = left - 1
                else:
                    dp[left][right] = dp[left][right + 1] + 1
                    nxt[left][right] = right + 1
    left, right = 0, n - 1
    while left <= right:
        if left == right:
            return 'Alice' if dp[left][right] % 2 == 1 else 'Bob'
        if nxt[left][right] == left - 1:
            left += 1
        else:
            right -= 1
    return 'Bob' if dp[0][n - 1] % 2 == 1 else 'Alice'
