def game_winner(A):

    Alice_turn = True
    left, right = 0, len(A) - 1
    last_picked = -1

    while left <= right:
        if A[left] > last_picked and (A[right] <= last_picked or A[left] < A[right]):
            last_picked = A[left]
            left += 1
        elif A[right] > last_picked and (A[left] <= last_picked or A[right] < A[left]):
            last_picked = A[right]
            right -= 1
        elif A[left] > last_picked:
            last_picked = A[left]
            left += 1
        elif A[right] > last_picked:
            last_picked = A[right]
            right -= 1
        else:
            break
        Alice_turn = not Alice_turn 

    return 'Alice' if Alice_turn else 'Bob'
