def beautiful_coloring(arr):
    def is_beautiful(d):
        prev_color = None
        for num in arr:
            current_color = 'red' if num % d == 0 else 'blue'
            if current_color == prev_color:
                return False
            prev_color = current_color
        return True

    for d in range(1, max(arr) + 1):
        if is_beautiful(d):
            return d
    return 0
