import datetime

def check_date(date_str):
    try:
        datetime.datetime.strptime(date_str, '%m-%d-%Y')
        return True
    except ValueError:
        try:
            datetime.datetime.strptime(date_str, '%m/%d/%Y')
            return True
        except ValueError:
            return False

print(check_date('03-11-2000'))  # True
print(check_date('15-01-2012'))  # False
print(check_date('06/04/1998'))  # True
