def interval_intersection(interval_1, interval_2):
    int_1 = tuple(map(int, interval_1.strip('()').split(',')))
    int_2 = tuple(map(int, interval_2.strip('()').split(',')))

    start = max(int_1[0], int_2[0])
    end = min(int_1[1], int_2[1])

    if start > end:
        return ()
    elif start == end:
        return (start, end)
    else:
        return (start, end)
