def convert_to_roman(num):
    value_map = [(1000, 'M'), (900, 'CM'), (500, 'D'), (400, 'CD'), (100, 'C'), (90, 'XC'),
                 (50, 'L'), (40, 'XL'), (10, 'X'), (9, 'IX'), (5, 'V'), (4, 'IV'), (1, 'I')]

    roman_num = ''

    for i, numeral in value_map:
        while num >= i:
            roman_num += numeral
            num -= i
    return roman_num
