def int_to_roman(input):
    if isinstance(input, int) and 0 < input < 4000:
        integer_to_numeral = [
            (1000, 'M'), (900, 'CM'), (500, 'D'), (400, 'CD'), (100, 'C'),
            (90, 'XC'),(50, 'L'), (40, 'XL'), (10, 'X'), (9, 'IX'),
            (5, 'V'), (4, 'IV'), (1, 'I')
        ]

        result = []
        for integer, numeral in integer_to_numeral:
            count = input // integer
            result.append(numeral * count)
            input -= integer * count
        return ''.join(result)
    else:
        raise ValueError('Input out of range (1-3999)')
