def calculate_expression(integers, operators):
    result = integers[0]
    for i in range(1, len(integers)):
        if operators[i-1] == '+':
            result += integers[i]
        elif operators[i-1] == '-':
            result -= integers[i]
        elif operators[i-1] == '*':
            result *= integers[i]
        elif operators[i-1] == '/':
            result /= integers[i]
        elif operators[i-1] == '**':
            result **= integers[i]
    return result

print(calculate_expression([2, 3, 4, 5], ['+', '*', '-']))
print(calculate_expression([2, 3, 4, 5], ['**', '*', '+']))
