def convert_base(num, base):
    symbols = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
    if base < 2 or base > 36 or base == 10: 
        raise ValueError('The base must be between 2 and 36.')
    remainder = num % base
    quotient = num // base
    if quotient == 0: 
        return symbols[remainder]
    else: 
        return convert_base(quotient, base) + symbols[remainder]
