def nth_fibonacci(n):
    if n<=0:
        print("Incorrect input")
    elif n==1:
        return 1
    elif n==2:
        return 1
    else:
        a = 1
        b = 1
        for i in range(n-2):
            a, b = b, a+b
        return b

