def deepest_level_of_nesting(s):
    max_nested = current_nested = 0
    for char in s:
        if char == '(':
            current_nested += 1
            max_nested = max(max_nested, current_nested)
        elif char == ')':
            if current_nested > 0:
                current_nested -= 1
            else:
                return -1
    if current_nested != 0:
        return -1
    return max_nested
