def caesar_cipher(input_string, shift):
    encoded_string = ''
    for char in input_string:
        if char.isalpha():
            ascii_offset = ord('a') if char.islower() else ord('A')
            shifted_position = (ord(char) - ascii_offset - int(shift)) % 26
            new_char = chr(shifted_position + ascii_offset)
            encoded_string += new_char
        else:
            encoded_string += char
    return encoded_string
