def caesar_cipher(input_string, shift):
    encrypted_string = ""
    for i in input_string:
        if i.isalpha():  # check if character is a letter
            stay_in_alphabet = ord(i) + shift 
            if i.isupper(): 
                final_letter = stay_in_alphabet if ord('A') <= stay_in_alphabet <= ord('Z') else stay_in_alphabet - 26
            else:
                final_letter = stay_in_alphabet if ord('a') <= stay_in_alphabet <= ord('z') else stay_in_alphabet - 26
            encrypted_string += chr(final_letter)
        else:
            encrypted_string += i
    return encrypted_string
