def caesar_cipher(input_string, shift):
    alpha = 'abcdefghijklmnopqrstuvwxyz'
    result = ''

    for char in input_string:
        if char.isalpha():
            ind = (alpha.index(char.lower()) - int(shift))%26
            result += alpha[ind].upper() if char.isupper() else alpha[ind]
        else:
            result += char
    return result

# Test the function 
print(caesar_cipher('hi', '2'))           # Output: fg
print(caesar_cipher('asdfghjkl', '4'))    # Output: wozbcdfgh
