def caesar_cipher(text, shift):
    encrypted_text = ''
    for char in text:
        if char.isalpha():
            ascii_offset = ord('a') if char.islower() else ord('A')
            encrypted_char = chr((ord(char) - ascii_offset - int(shift)) % 26 + ascii_offset)
        else:
            encrypted_char = char
        encrypted_text += encrypted_char
    return encrypted_text

