def min_difference(a, b, c):
    total_time = a + b * 2 + c * 3
    half_time = (total_time + 1) // 2

    dp = [0] * (half_time + 1)

    for time in (1, 2, 3):
        for i in range(half_time, time-1, -1):
            dp[i] = max(dp[i], dp[i - time] + time)

    return abs(total_time - dp[half_time] * 2)
