def min_difference(a, b, c):
    total_time = a + b * 2 + c * 3
    half = total_time // 2
    dp = [0] * (half + 1)

    for i in range(a+b+c+1):
        for j in range(half, -1 , -1):
            if i < a and j >= 1: dp[j] = max(dp[j], dp[j-1] + 1)
            if a <= i < a + b and j >= 2: dp[j] = max(dp[j], dp[j-2] + 2)
            if a + b <= i < a + b + c and j >= 3: dp[j] = max(dp[j], dp[j-3] + 3)

    return abs(half - dp[half] * 2 if total_time % 2 == 0 else half - dp[half] * 2 + 1)
