def min_difference_in_concert_duration(a, b, c):
    diff = [0]*60
    diff[0] = 1
    for _ in range(a):       #1-minute songs
        for j in range(59,-1,-1):
            if diff[j]:
                diff[j+1] = 1
    for _ in range(b):       #2-minute songs
        for j in range(58,-1,-2):
            if diff[j]:
                diff[j+2] = 1
    for _ in range(c):       #3-minute songs
        for j in range(57,-1,-3):
            if diff[j]:
                diff[j+3] = 1
    total_minutes = a + 2*b + 3*c
    for i in range(total_minutes // 2, -1, -1):
        if diff[i]:
            return total_minutes - 2*i
    return 0
