def min_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    max_first_concert_duration = total_duration // 2

    dp = [0] * (max_first_concert_duration + 1)

    for _ in range(c):
        for j in range(max_first_concert_duration, 2, -1):
            dp[j] = max(dp[j], dp[j - 3] + 3 if j - 3 >= 0 else 0)

    for _ in range(b):
        for j in range(max_first_concert_duration, 1, -1):
            dp[j] = max(dp[j], dp[j - 2] + 2 if j - 2 >= 0 else 0)

    for _ in range(a):
        for j in range(max_first_concert_duration, 0, -1):
            dp[j] = max(dp[j], dp[j - 1] + 1 if j - 1 >= 0 else 0)

    return abs(total_duration - 2 * dp[-1])
