def min_concert_diff(a, b, c):
    total_time = a + b * 2 + c * 3
    min_diff = total_time
    for i in range(c+1):
        for j in range(min(b, total_time//2 - i*3) + 1):
            k = min(a, total_time//2 - i*3 - j*2)
            diff = abs(total_time - (i*3 + j*2 + k) * 2)
            if diff < min_diff:
                min_diff = diff
    return min_diff
