def determine_winner(A):
    last = -1
    moves = 0
    while A:
        if len(A) == 1:
            return 'Alice' if moves % 2 == 0 else 'Bob'
        if A[0] > last and (A[-1] <= last or A[0] <= A[-1]):
            last = A.pop(0)
            moves += 1
        elif A[-1] > last and (A[0] <= last or A[-1] >= A[0]):
            last = A.pop(-1)
            moves += 1
        else:
            return 'Alice' if moves % 2 == 0 else 'Bob'
    return 'Alice' if moves % 2 == 0 else 'Bob'
