def game_winner(arr):
    alice_turn = True
    last_taken = -1

    while len(arr) > 0:
        # take from the left
        if arr[0] > last_taken and (len(arr) == 1 or arr[0] >= arr[-1]):
            last_taken = arr.pop(0)
            alice_turn = not alice_turn

        # take from the right
        elif arr[-1] > last_taken and (len(arr) == 1 or arr[-1] > arr[0]):
            last_taken = arr.pop(-1)
            alice_turn = not alice_turn

        # both sides are less than the last taken number
        else:
            break

    if alice_turn:
        return 'Bob'
    else:
        return 'Alice'
