def game_winner(array):
    n = len(array) 
    l = 0
    r = n - 1
    last = -1
    for i in range(n):
        if array[l] > last and (array[r] <= last or array[l] < array[r]):
            last = array[l]
            l += 1
        elif array[r] > last and (array[l] <= last or array[r] < array[l]):
            last = array[r]
            r -= 1
        else:
            break
    return 'Alice' if i % 2 == 0 else 'Bob'
